SELECT sum(decode(event,'SQL*Net message to client',average_wait,'SQL*Net message to dblink',average_wait,'SQL*Net more data to client',average_wait,'SQL*Net more data to dblink',average_wait,'SQL*Net break/reset to client',average_wait,'SQL*Net break/reset to dblink',average_wait,0))/100 SQLNET FROM GV$system_event WHERE event not in ('SQL*Net message from client','SQL*Net more data from client','pmon timer', 'rdbms ipc message','rdbms ipc reply', 'smon timer');